    html.appearance-ttb div#bibi-arrow-forward:after {
      bottom: -32px;
      background: linear-gradient(to bottom, rgba(128, 128, 128, 0), rgba(128, 128, 128, 0) 25%, rgba(128, 128, 128, 0.125)); }
  html.appearance-ttb div#bibi-arrow-forward.available.hover:after, html.appearance-ttb div#bibi-arrow-forward.available.glowing:after {
    bottom: 0; }
  html.appearance-ttb div#bibi-arrow-forward.available.glowing:before {
