    bottom: 8px; }

html.appearance-ltr div#bibi-arrow-back, html.appearance-rtl div#bibi-arrow-forward {
  /* ← */ }
  html.appearance-ltr div#bibi-arrow-back, html.appearance-rtl div#bibi-arrow-forward {
