html {
  overflow: hidden;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  font-family: sans-serif;
  -webkit-font-smoothing: subpixel-antialiased;
  -webkit-text-size-adjust: 100%;
  background: #404040;
  /*&[data-bibi-cursor="left"],
	&[data-bibi-cursor="right"],
	&[data-bibi-cursor="top"],
	&[data-bibi-cursor="bottom"] { cursor: pointer; }*/
  user-select: none; }
  html[data-bibi-cursor="left"] {
    cursor: w-resize; }
  html[data-bibi-cursor="right"] {
    cursor: e-resize; }
  html[data-bibi-cursor="top"] {
    cursor: n-resize; }
  html[data-bibi-cursor="bottom"] {
    cursor: s-resize; }
  html[data-bibi-cursor="menu"] {
    cursor: cell; }
  html[data-bibi-cursor="no"] {
    cursor: not-allowed; }

body {
  overflow: hidden;
  width: 100%;
  height: 100%;
  background: transparent no-repeat center center;
  transition: opacity .25s ease; }
  body * {
    -webkit-tap-highlight-color: rgba(255, 255, 255, 0);
    -webkit-tap-highlight-color: transparent; }

a {
