  position: absolute;
  z-index: 1;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  padding-top: 64px;
  width: 100%;
  height: 100%;
  transition: .15s ease;
  transform-origin: 0 0;
  will-change: scroll-position, transform, opacity;
  padding-top: 0; }

.page-img,
.page-blank {
